import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MyComponent2.css'; // Import your CSS file for styling

const MyComponent2 = () => {
  const [yourData, setYourData] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/display_3latestvalue'); // Thay đổi URL API của bạn ở đây
      setYourData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const startPolling = () => {
    const pollingInterval = setInterval(fetchData, 1000); // Thay đổi khoảng thời gian theo ý của bạn (ví dụ, 5000 cho mỗi 5 giây)
    return () => clearInterval(pollingInterval);
  };

  useEffect(() => {
    fetchData(); // Lấy dữ liệu khi component được mount
    const stopPolling = startPolling(); // Bắt đầu polling

    return () => {
      stopPolling(); // Dừng polling khi component bị unmount
    };
  }, []);

  return (
    <div className="my-data-container">
      <h1 className="my-data-title">
        <span>Your Data: </span>
        {yourData.length > 0 ? (
          <span className="my-value">{yourData[0].yourField} yourUnit</span>
        ) : (
          <span className="no-data-message">No data available.</span>
        )}
      </h1>
    </div>
  );
};

export default MyComponent2;
