import React, { useState, useEffect } from 'react';
import Form from '@rjsf/core';
import validator from '@rjsf/validator-ajv8';
import axios from 'axios';
import './MyComponent.css';

const schema = {
  title: 'Lưu trữ dữ liệu khối lượng',
  type: 'object',
  required: ['ten', 'khoiLuong'],
  properties: {
    ten: { type: 'string', title: 'Tên Sản Phẩm:' },
    khoiLuong: { type: 'number', title: 'Khối Lượng:' },
  },
};

const uiSchema = {
  'ui:title': (
    <div style={{ fontSize: '2em', fontWeight: 'bold' }}>
      Lưu trữ dữ liệu khối lượng
    </div>
  ),
  ten: {
    'ui:widget': 'text',
    'ui:options': { classNames: 'custom-input' },
  },
};

const setpointSchema = {
  title: 'Cập nhật giá trị Setpoint',
  type: 'object',
  required: ['setpoint'],
  properties: {
    setpoint: { type: 'number', title: 'Setpoint:' },
  },
};

const setpointUiSchema = {
  'ui:title': (
    <div style={{ fontSize: '2em', fontWeight: 'bold' }}>
      Cập nhật giá trị setpoint
    </div>
  ),
  setpoint: {
    'ui:options': { classNames: 'custom-input' },
  },
};

const MyComponent = () => {
  const [formData, setFormData] = useState({});
  const [setpointFormData, setSetpointFormData] = useState({});
  const [updatedSetpoint, setUpdatedSetpoint] = useState(null);

  const handleDataSubmit = async ({ formData }) => {
    try {
      console.log('Dữ liệu gửi đi:', formData);
      const response = await axios.post(
        'https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/post_1',
        formData
      );

      console.log('Kết quả từ server:', response.data);
      setFormData({});
      // Assume the response includes the updated setpoint value
      setUpdatedSetpoint(response.data.setpoint);
    } catch (error) {
      console.error('Lỗi khi gửi dữ liệu:', error);
    }
  };

  const handleSetpointSubmit = async ({ formData }) => {
    try {
      console.log('Dữ liệu setpoint gửi đi:', formData);
      const response = await axios.post(
        'https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/post_setpoint',
        formData
      );

      console.log('Kết quả từ server (setpoint):', response.data);
      setSetpointFormData({});
    } catch (error) {
      console.error('Lỗi khi gửi dữ liệu setpoint:', error);
    }
  };

  useEffect(() => {
    // Fetch the updated setpoint value after submitting data
    const fetchUpdatedSetpoint = async () => {
      try {
        const response = await axios.get(
          'https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/get_setpoint'
        );
        setUpdatedSetpoint(response.data.setpoint);
      } catch (error) {
        console.error('Lỗi khi lấy giá trị setpoint:', error);
      }
    };

    // Call the function to fetch the setpoint value after data submission
    if (updatedSetpoint === null) {
      fetchUpdatedSetpoint();
    }
  }, [updatedSetpoint]);

  return (
    <div className="auth-form-container">
      <Form
        schema={schema}
        validator={validator}
        formData={formData}
        onChange={({ formData }) => setFormData(formData)}
        onSubmit={handleDataSubmit}
        uiSchema={uiSchema}
      />
      <Form
        schema={setpointSchema}
        validator={validator}
        formData={setpointFormData}
        onChange={({ formData }) => setSetpointFormData(formData)}
        onSubmit={handleSetpointSubmit}
        uiSchema={setpointUiSchema}
      />
      {updatedSetpoint !== null && (
        <div>
          <h3>Updated Setpoint Value:</h3>
          <p>{updatedSetpoint}</p>
        </div>
      )}
    </div>
  );
};

export default MyComponent;
