import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MongoDataComponent = () => {
  const [weightData, setWeightData] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/get_updatedvalue');
      setWeightData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const startPolling = () => {
    const pollingInterval = setInterval(fetchData, 2000); // Adjust the interval as needed (e.g., 5000 for every 5 seconds)
    return () => clearInterval(pollingInterval);
  };

  useEffect(() => {
    fetchData(); // Fetch data when the component mounts
    const stopPolling = startPolling(); // Start polling

    return () => {
      stopPolling(); // Stop polling when the component is unmounted
    };
  }, []);

  return (
    <div>
      <h1>Weight Data</h1>
      {weightData.length > 0 ? (
        <ul>
          {weightData.map((item, index) => (
            <li key={index}>{item.weight}</li>
          ))}
        </ul>
      ) : (
        <p>No weight data available.</p>
      )}
    </div>
  );
};

export default MongoDataComponent;
