import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MongoDataComponent = () => {
  const [weightData, setWeightData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Thay đổi địa chỉ API thành địa chỉ MongoDB Atlas của bạn
        const response = await axios.get('https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/get_updatedvalue');
        setWeightData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Weight Data</h1>
      {weightData.length > 0 ? (
        <ul>
          {weightData.map((item, index) => (
            <li key={index}>{item.weight}</li>
          ))}
        </ul>
      ) : (
        <p>No weight data available.</p>
      )}
    </div>
  );
};

export default MongoDataComponent;
