import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MongoDataComponent.css'; // Import your CSS file for styling

const MongoDataComponent = () => {
  const [weightData, setWeightData] = useState([]);
  const [setpoint, setSetpoint] = useState('');

  const fetchData = async () => {
    try {
      const response = await axios.get('https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/get_updatedvalue');
      setWeightData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const startPolling = () => {
    const pollingInterval = setInterval(fetchData, 5000);
    return () => clearInterval(pollingInterval);
  };

  const handleSetpointChange = (event) => {
    setSetpoint(event.target.value);
  };

  const handleSetpointSubmit = async (event) => {
    event.preventDefault();

    try {
      await axios.update('https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/update_setpoint', {
        setpoint: parseFloat(setpoint),
      });

      // Nếu cần, sau khi gửi thành công, bạn có thể làm mới dữ liệu hoặc thực hiện các hành động khác.
    } catch (error) {
      console.error('Error setting setpoint:', error);
    }
  };

  useEffect(() => {
    fetchData();
    const stopPolling = startPolling();

    return () => {
      stopPolling();
    };
  }, []);

  return (
    <div className="weight-data-container">
      <h1 className="weight-data-title">
        <span>Weight Data: </span>
        {weightData.length > 0 ? (
          <span className="weight-value">{weightData[0].weight} gram</span>
        ) : (
          <span className="no-data-message">No weight data available.</span>
        )}
      </h1>

      <form onSubmit={handleSetpointSubmit}>
        <label>
          Setpoint:
          <input type="text" value={setpoint} onChange={handleSetpointChange} />
        </label>
        <button type="submit">Set Setpoint</button>
      </form>
    </div>
  );
};

export default MongoDataComponent;
