// Snowflake.js
import React, { useEffect, useRef } from 'react';
import './Snowflake.css';

const Snowflake = () => {
  const snowflakeRef = useRef(null);

  useEffect(() => {
    const startPositionX = Math.random() * window.innerWidth;
    const rotation = Math.random() * 360;
    const speed = Math.random() * 2 + 1;

    const animateSnowflake = () => {
      const currentY = snowflakeRef.current.getBoundingClientRect().top;

      if (currentY < window.innerHeight) {
        snowflakeRef.current.style.top = `${currentY + speed}px`;
        requestAnimationFrame(animateSnowflake);
      } else {
        // Nếu bông tuyết rơi xuống cuối trang, tái tạo nó ở trên và chọn vị trí ngẫu nhiên
        snowflakeRef.current.style.top = '0px';
        snowflakeRef.current.style.left = `${Math.random() * window.innerWidth}px`;

        // Thêm độ trễ trước khi tái tạo để giảm tải
        setTimeout(() => {
          animateSnowflake();
        }, 2000);
      }
    };

    snowflakeRef.current.style.left = `${startPositionX}px`;
    snowflakeRef.current.style.transform = `rotate(${rotation}deg)`;

    animateSnowflake();
  }, []); // Thêm [] để chỉ gọi useEffect khi component được mount

  return <div ref={snowflakeRef} className="snowflake" />;
};

export default Snowflake;
