// Snowflake.js
import React, { useEffect, useRef } from 'react';
import './Snowflake.css';

const Snowflake = () => {
  const snowflakeRef = useRef(null);

  useEffect(() => {
    const animateSnowflake = () => {
      const currentY = snowflakeRef.current.getBoundingClientRect().top;
      const speed = Math.random() * 2 + 1;

      if (currentY < window.innerHeight) {
        snowflakeRef.current.style.top = `${currentY + speed}px`;
        requestAnimationFrame(animateSnowflake);
      } else {
        // Nếu bông tuyết rơi xuống cuối trang, tái tạo nó ở trên
        snowflakeRef.current.style.top = '0px';
      }
    };

    animateSnowflake();
  }, []);

  return <div ref={snowflakeRef} className="snowflake" />;
};

export default Snowflake;
