import React, { useState } from 'react';
import Form from '@rjsf/core';
import validator from '@rjsf/validator-ajv8';
import axios from 'axios';

const formDataInitialState = {};
const setpointFormDataInitialState = {};

const MyComponent = () => {
  const [formData, setFormData] = useState(formDataInitialState);
  const [setpointFormData, setSetpointFormData] = useState(setpointFormDataInitialState);

  const handleSubmit = async (endpoint, formData, setFormData) => {
    try {
      console.log(`Dữ liệu gửi đi:`, formData);
      const response = await axios.post(`https://ap-southeast-1.aws.data.mongodb-api.com/app/data-fmace/endpoint/${endpoint}`, formData);
      console.log(`Kết quả từ server:`, response.data);
      setFormData({});
    } catch (error) {
      console.error(`Lỗi khi gửi dữ liệu:`, error);
    }
  };

  const handleDataSubmit = ({ formData }) => {
    handleSubmit('post_1', formData, setFormData);
  };

  const handleSetpointSubmit = ({ formData }) => {
    handleSubmit('post_setpoint', formData, setSetpointFormData);
  };

  const commonUiOptions = {
    classNames: 'custom-input',
    style: { fontSize: '1.3em', fontWeight: 'bold', display: 'grid', gridTemplateColumns: '80px auto', marginTop: '5px' },
  };

  const uiSchema = {
    'ui:title': <div style={{ fontSize: '2em', fontWeight: 'bold', textAlign: 'justify' }}>Lưu trữ dữ liệu khối lượng</div>,
    ten: { 'ui:widget': 'text', 'ui:options': commonUiOptions },
    khoiLuong: { 'ui:widget': 'text', 'ui:options': commonUiOptions },
  };

  const setpointUiSchema = {
    'ui:title': <div style={{ fontSize: '2em', fontWeight: 'bold', textAlign: 'justify' }}>Cập nhật giá trị Setpoint</div>,
    setpoint: { 'ui:options': { ...commonUiOptions, style: { ...commonUiOptions.style, gridTemplateColumns: '800px auto' } } },
  };

  return (
    <div className="auth-form-container">
      <Form schema={{ type: 'object' }} uiSchema={setpointUiSchema} formData={setpointFormData} onChange={({ formData }) => setSetpointFormData(formData)} onSubmit={handleSetpointSubmit} />
      <Form schema={{ type: 'object' }} uiSchema={uiSchema} formData={formData} onChange={({ formData }) => setFormData(formData)} onSubmit={handleDataSubmit} />
    </div>
  );
};

export default MyComponent;
